(()=>{const e=document.querySelector("#submit"),t=document.querySelector("#btn"),c=document.querySelector("#account"),u=document.querySelector("#pwd");e.addEventListener("click",()=>{const n=c.value,o=u.value,r=t.value;chrome.tabs.query({active:!0,currentWindow:!0},s=>{chrome.tabs.sendMessage(s[0].id,{type:"manual",account:n,pwd:o,btn:r},()=>{alert("\u4FEE\u6539\u6210\u529F")})})})})();
